import React,{useState} from 'react';
const App =() => {
    const [name,setName] = useState();
    const [lname,setLName] = useState();
    const [myfullName,setFullName] = useState();
    const [myLastName,setLastName] = useState();
    const inputEvent = (event) =>{
        console.log(event.target.value);
        setName(event.target.value);
    };
    const inputLastEvent = (event) =>{
        console.log(event.target.value);
        setLName(event.target.value);
    };
    const onSubmit=(event) =>{
        event.preventDefault();
        setFullName(name);
        setLastName(lname);
    }
    return(
        <>
<div className='main_div'>
<form onSubmit={onSubmit}>
    <h1>Hello {myfullName}{myLastName}</h1>
    <input type="text" name="fName" placeholder="Enter YOur Name" 
     onChange={inputEvent}
         value={name}
     />
     <input type="text" name='lName' placeholder="Enter YOur LastName" 
     onChange={inputLastEvent}
         value={lname}
     />

    <button >Click Me </button>
    </form>
</div>

        </>
    );
}
export default App;
