import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css"


const images1='https://picsum.photos/200/300';
const images2='https://picsum.photos/250/300';
const images3='https://picsum.photos/300/300';
const name="Kumar";

let curdate = new Date(2020,5,2,4);
curdate = curdate.getHours();
let greeting="";
if (curdate>=1 && curdate<= 11)
{
    greeting ="Good Morning";
}
else if(curdate>=12 && curdate <=19)
{
    greeting ="Good Afternoon";
}
else{
    greeting="Good Night";
}
const heading = { color: '#fa9191',textAlign: "center",textTransform: "capitalize",fontWeight: "bold",textShadow: "0px 2px 4px #ffe9c5",fontFamily:" 'Josefin Sans', sansSerif",margin: "50px 0 " };
const msg = { color: 'green',textAlign: "center",textTransform: "capitalize",fontWeight: "bold",textShadow: "0px 2px 4px #ffe9c5",fontFamily:" 'Josefin Sans', sansSerif",margin: "50px 0 " };
ReactDOM.render([<div  ><h1>Hello Sir, <p style={msg}>{greeting}</p></h1></div>,
,
<div className='images'><img src={images1} alt='randomimages'/>,<img src={images2} alt='randomimages'/>,<img src={images3} alt='randomimages'/></div>

],document.getElementById("kumar"));
